import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';

function Header() {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const location = useLocation();

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 20);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    return (
        <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
            <div className="container">
                <nav className="nav-container">
                    <Link to="/" className="logo">
                        <span className="logo-text-car">Car</span>
                        <span className="logo-text-park">Park</span>
                    </Link>

                    <button 
                        className="menu-toggle"
                        onClick={toggleMenu}
                        aria-label="Toggle menu"
                    >
                        <span className="menu-icon"></span>
                    </button>

                    <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
                        <Link 
                            to="/" 
                            className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
                        >
                            Home
                        </Link>
                        <Link 
                            to="/search" 
                            className={`nav-link ${location.pathname === '/search' ? 'active' : ''}`}
                        >
                            Find Parking
                        </Link>
                        <Link 
                            to="/booking-history" 
                            className={`nav-link ${location.pathname === '/booking-history' ? 'active' : ''}`}
                        >
                            My Bookings
                        </Link>
                        <Link 
                            to="/favorites" 
                            className={`nav-link ${location.pathname === '/favorites' ? 'active' : ''}`}
                        >
                            Favorites
                        </Link>

                        <div className="nav-auth">
                            <Link 
                                to="/login" 
                                className={`btn btn-outline ${location.pathname === '/login' ? 'active' : ''}`}
                            >
                                Login
                            </Link>
                            <Link 
                                to="/signup" 
                                className={`btn btn-primary ${location.pathname === '/signup' ? 'active' : ''}`}
                            >
                                Sign Up
                            </Link>
                        </div>
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
