import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Layout from './components/Layout';

// Auth Pages
import Login from './pages/Login';
import Signup from './pages/Signup';

// Driver Pages
import DriverDashboard from './pages/driver/Dashboard';
import VehicleManagement from './pages/driver/VehicleManagement';
import ParkingSearch from './pages/driver/ParkingSearch';

// Manager Pages
import ManagerDashboard from './pages/manager/Dashboard';
import ParkingManagement from './pages/manager/ParkingManagement';

function App() {
  const isAuthenticated = localStorage.getItem('token');
  const userRole = localStorage.getItem('userRole');

  const PrivateRoute = ({ children, allowedRole }) => {
    if (!isAuthenticated) {
      return <Navigate to="/login" />;
    }
    if (allowedRole && userRole !== allowedRole) {
      return <Navigate to="/" />;
    }
    return children;
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />

        <Route path="/" element={<Layout />}>
          {/* Driver Routes */}
          <Route path="driver" element={
            <PrivateRoute allowedRole="driver">
              <DriverDashboard />
            </PrivateRoute>
          } />
          <Route path="driver/vehicles" element={
            <PrivateRoute allowedRole="driver">
              <VehicleManagement />
            </PrivateRoute>
          } />
          <Route path="driver/search" element={
            <PrivateRoute allowedRole="driver">
              <ParkingSearch />
            </PrivateRoute>
          } />

          {/* Manager Routes */}
          <Route path="manager" element={
            <PrivateRoute allowedRole="manager">
              <ManagerDashboard />
            </PrivateRoute>
          } />
          <Route path="manager/parking" element={
            <PrivateRoute allowedRole="manager">
              <ParkingManagement />
            </PrivateRoute>
          } />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
