import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';

function Header() {
    const [isScrolled, setIsScrolled] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const location = useLocation();
    const navigate = useNavigate();
    const isAuthenticated = localStorage.getItem('token');
    const userRole = localStorage.getItem('userRole');

    useEffect(() => {
        const handleScroll = () => {
            setIsScrolled(window.scrollY > 20);
        };

        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('userRole');
        navigate('/login');
    };

    const renderNavLinks = () => {
        if (!isAuthenticated) {
            return (
                <>
                    <Link to="/" className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}>
                        Home
                    </Link>
                    <div className="nav-auth">
                        <Link to="/login" className={`btn btn-outline ${location.pathname === '/login' ? 'active' : ''}`}>
                            Login
                        </Link>
                        <Link to="/signup" className={`btn btn-primary ${location.pathname === '/signup' ? 'active' : ''}`}>
                            Sign Up
                        </Link>
                    </div>
                </>
            );
        }

        if (userRole === 'driver') {
            return (
                <>
                    <Link to="/driver" className={`nav-link ${location.pathname === '/driver' ? 'active' : ''}`}>
                        Dashboard
                    </Link>
                    <Link to="/driver/search" className={`nav-link ${location.pathname === '/driver/search' ? 'active' : ''}`}>
                        Find Parking
                    </Link>
                    <Link to="/driver/vehicles" className={`nav-link ${location.pathname === '/driver/vehicles' ? 'active' : ''}`}>
                        My Vehicles
                    </Link>
                    <button onClick={handleLogout} className="btn btn-outline">
                        Logout
                    </button>
                </>
            );
        }

        if (userRole === 'manager') {
            return (
                <>
                    <Link to="/manager" className={`nav-link ${location.pathname === '/manager' ? 'active' : ''}`}>
                        Dashboard
                    </Link>
                    <Link to="/manager/parking" className={`nav-link ${location.pathname === '/manager/parking' ? 'active' : ''}`}>
                        Parking Management
                    </Link>
                    <button onClick={handleLogout} className="btn btn-outline">
                        Logout
                    </button>
                </>
            );
        }
    };

    return (
        <header className={`header ${isScrolled ? 'scrolled' : ''}`}>
            <div className="container">
                <nav className="nav-container">
                    <Link to={isAuthenticated ? `/${userRole}` : "/"} className="logo">
                        <span className="logo-text-car">Car</span>
                        <span className="logo-text-park">Park</span>
                    </Link>

                    <button 
                        className="menu-toggle"
                        onClick={toggleMenu}
                        aria-label="Toggle menu"
                    >
                        <span className="menu-icon"></span>
                    </button>

                    <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
                        {renderNavLinks()}
                    </div>
                </nav>
            </div>
        </header>
    );
}

export default Header;
